<script>
    $(document).ready(function(){
        
        $('#navbar_two').hide();
        $("#navbar_two").visibility({
            once: false,
            onBottomPassed: function(){
                $('#nav').transition('fade out');
                $('#navbar_two').transition('fade in');
            },
            onBottomPassedReverse: function() {
                $('#navbar_two').transition('fade out');
                $('#nav').transition('fade in');
            }
        });
    });
</script>
